mapper.update:
UPDATE tb_delivery
SET 
	delivery_num = #{deliveryNum}
WHERE
	order_detail_code = #{orderDetailCode};

mapper.select:
SELECT od.order_datetime ,od.order_detail_code ,od.order_amount ,od.regular_price_subtotal ,g.g_name ,d.d_get_name ,d.d_get_addr ,d.d_get_phone ,d.d_memo ,g.g_code ,os.order_status_standard_code ,oss.order_status_standard_details ,d.delivery_enter ,d.delivery_num ,gmi.main_image_original_name ,gmi.main_image_path FROM tb_order_detail AS od INNER join tb_enterprise AS ent ON ent.enter_code = od.enter_code INNER join tb_goods AS g ON od.g_code = g.g_code INNER JOIN tb_goods_main_image AS gmi ON gmi.main_image_idx = g.g_main_image LEFT JOIN tb_delivery AS d on d.order_detail_code = od.order_detail_code left join tb_order_status AS os on os.order_status_code = d.order_status_code left JOIN tb_order_status_standard AS oss on os.order_status_standard_code = oss.order_status_standard_code
ent.member_id = #{memberId}
AND ${sk} LIKE CONCAT('%', #{sv}, '%')
order by od.order_datetime desc;
