mapper.update:
UPDATE
	tb_order_discount_management
SET 
	discount_name = #{discountName}
	,discount_rate = #{discountRate}
	,discount_price = #{discountPrice}
	,discount_expiration_date = #{discountExpirationDate}
	,min_order_price = #{minOrderPrice}
	,max_discount_price = #{maxDiscountPrice}
WHERE
	order_discount_code = #{orderDiscountCode};

mapper.insert:
INSERT INTO tb_order_discount_management (
	order_discount_code
	,discount_rate
	,discount_price
	,discount_name
	,discount_expiration_date
	,min_order_price
	,max_discount_price
	,g_production_reg_datetime
)VALUES(
	#{orderDiscountCode}
	,#{discountRate}
	,#{discountPrice}
	,#{discountName}
	,date_add(NOW(), INTERVAL 1 MONTH)
	,#{minOrderPrice}
	,#{maxDiscountPrice}
	,NOW()
);
