mapper.update:
UPDATE tb_goods
SET
	g_cate_code=#{goodsCateCode}
	,g_name=#{goodsName}
	,g_price=#{goodsPrice}
	,goods_discount_code=#{goodsDiscountCode}
	,g_discount=#{goodsDiscountedPrice}
	
		,g_netweight=#{goodsNetWeight}
	
	
		,g_netweightunit=#{goodsNetWeightUnit}
	
	
		,g_main_image=#{goodsMainImageCode}
	
	
		,g_info_image=#{goodsInfoImageCode}
	
	,g_stock=${goodsStock}
	,g_delivery_charge=${goodsDeliveryCharge}
	,g_activation=${goodsActivation}
WHERE 
	g_code = #{goodsCode};

UPDATE tb_menu_information
SET
	menu_name = #{menuName}
WHERE 
	menu_code = #{menuCode};

mapper.select:
SELECT gdm.goods_discount_code ,gdm.discount_price ,gdm.discount_rate ,gdm.discount_name ,gdm.discount_expiration_date ,gdm.member_id ,gdm.g_pro_reg_datetime ,e.enter_name as e_enter_name ,e.enter_number as e_enter_number FROM tb_goods_discount_management AS gdm LEFT JOIN tb_enterprise AS e ON e.enter_code = gdm.enter_code
${sk} LIKE CONCAT('%', #{sv}, '%')
${sk} BETWEEN #{minNum} AND #{maxNum}
${sk} <= #{maxNum}
${sk} >= #{minNum}
${sk} BETWEEN #{minDate} AND #{maxDate}
${sk} <= #{maxDate}
${sk} >= #{minDate}

mapper.update:
UPDATE tb_goods_discount_management AS gdm
SET
	gdm.discount_price = ${goodsDiscountPrice}
	,gdm.discount_rate = ${goodsDiscountRate}
	,gdm.discount_name = #{goodsDiscountName}
	,gdm.discount_expiration_date = #{goodsDiscountExpirationDate}
WHERE 
	gdm.goods_discount_code = #{goodsDiscountCode}
